
Object.defineProperty(Function.prototype, 'delay', {value: function(t){
  setTimeout(this, t);
}});

Object.defineProperty(Function.prototype, 'sequence', {value: function(fn){
  var bfn = this;
  return function(){
    bfn();
    fn();
  }
}});

Object.defineProperty(Object.prototype, 'apply', {value: function(config){
  for(var f in config){
    this[f] = config[f];
  }
}});

Object.defineProperty(Object.prototype, 'applyIf', {value: function(config){
  for(var f in config){
    if(!this[f]){
      this[f] = config[f];
    }
  }
}});
